import TAPi18next from '../tap_i18next/tap_i18next-1.7.3.js';

import {
  globals
} from './globals';

fallback_language = globals.fallback_language;

TAPi18next.init({
  resStore: {},
  fallbackLng: globals.fallback_language,
  useCookie: false
});

