      var i, lang_tag, languages, len, ref;
      if (!this._enabled()) {
        return null;
      }
      languages = {};
      ref = this._getProjectLanguages();
      for (i = 0, len = ref.length; i < len; i++) {
        lang_tag = ref[i];
        languages[lang_tag] = {
          name: this.languages_names[lang_tag][1],
          en: this.languages_names[lang_tag][0]
