			</Modal.Content>
			<Modal.Footer>
				<ButtonGroup align='end'>
					<Button
						onClick={(): void => {
							onClose(null);
						}}
					>
						{t('Cancel')}
					</Button>
					<Button primary disabled={!hasUnsavedChanges || !canSave} onClick={onSave}>
						{t('Save')}
					</Button>
				</ButtonGroup>
			</Modal.Footer>
		</Modal>
	);
};

export default memo(CreateCannedResponseModal);
