			data: Session.get('flex-nav-data'),
		};
	}

	openFlex(callback = (): void => undefined): void {
		if (!this.initiated) {
			this.openQueue.push({
				config: this.getFlex(),
				callback,
			});
			return;
		}
		if (this.animating === true) {
			return;
		}
		this.toggleFlex(1, callback);
	}

	init(): void {
		this.sideNav = $('.sidebar');
		this.flexNav = this.sideNav.find('.flex-nav');
